    field("__typename"),
    field("login"),
    inlineFragment("User", [field("id")]),
    inlineFragment("Organization", [field("id")]),
    inlineFragment("Bot", [field("id")]),
  ]),
];

// Type definitions.

type Body = Definition[];
declare function bodyToString(body: Body): string;

type Definition = QueryDefinition | FragmentDefinition;

// We only need opaque type handles; no need to embed the GraphQL type
// system into Flow.
type GraphQLType = string;

type QueryDefinition = {|
  +type: "QUERY",
  +name: string,
  +params: Parameter[],
  +selections: Selection[],
|};
type Parameter = {|+name: string, +type: GraphQLType|};

type FragmentDefinition = {|
  +type: "FRAGMENT",
  +name: string,
  +typeCondition: GraphQLType,
  +selections: Selection[],
|};

type Selection = Field | FragmentSpread | InlineFragment;
type Field = {|
  +type: "FIELD",
  +name: string,
  +args: Arguments,
  +selections: ?(Selection[]),
|};
type FragmentSpread = {|+type: "FRAGMENT_SPREAD", +fragmentName: string|};
type InlineFragment = {|
  +type: "INLINE_FRAGMENT",
  +typeCondition: ?GraphQLType,
  +selections: Selection[],
|};

type Arguments = {[string]: Value};
type Value = VariableValue | LiteralValue | EnumValue | ListValue | ObjectValue;
type VariableValue = {|+type: "VARIABLE", +data: string|};
type LiteralValue = {|
  +type: "LITERAL",
  +data: number | string | boolean | null,
|};
type EnumValue = {|+type: "ENUM", +data: string|};
type ListValue = {|+type: "LIST", +data: Value[]|};
type ObjectValue = {|+type: "OBJECT", +data: {[string]: Value}|};

// anamorphisms for convenience
declare function query(
  name: string,
  params: Parameter[],
  selections: Selection[]
): QueryDefinition;
declare function param(name: string, type: GraphQLType): Parameter;

declare function fragment(
  name: string,
  typeCondition: GraphQLType,
  selections: Selection[]
): FragmentDefinition;

declare function field(
  name: string,
  arguments: ?Arguments,
  selections: ?(Selection[])
): Field;
declare function fragmentSpread(name: string): FragmentSpread;
declare function inlineFragment(
  typeCondition: ?GraphQLType,
  selections: Selection[]
): InlineFragment;

declare function variable(name: string): VariableValue;
declare function literal(value: number | string | boolean | null): LiteralValue;
declare function enumLiteral(value: string): EnumValue;
declare function list(data: Value[]): ListValue;
declare function object(data: {[string]: Value}): ObjectValue;
```

